import React, { Component } from "react";
import data from "../../data/data.json";
import {
  Table,
  TableBody,
  TableContainer,
  TableCell,
  TableRow,
  TableHead,
  Select,
} from "@material-ui/core";

const styles = {
  container: {
    paddingLeft: 15,
    paddingRight: 15,
    margin: "50px auto",
    maxWidth: "100%"
  },
  tableCell: {
    padding: '0.75rem',
    verticalAlign: 'top',
    borderTop: "1px solid #dee2e6"
  }
};

export default class CartPage extends Component {
  state = {
    products: [],
    error: false,
  };

  componentDidMount() {
    const items = data.slice(0, 3);
    this.setState({ products: items });
  }

  async addToCart(id, quantity) {
    // Add data to cart api
    // Get The name, price and any special instructions
  }

  async removeFromCart(id, quantity) {
    // Remove data from cart api
  }

  render() {
    const { products } = this.state;

    return (
      <div id="cartPage" style={styles.container}>
        <Table aria-label="simple table">
          <TableHead>
            <TableRow id='cartColums'>
              <TableCell align="center">Product</TableCell>
              <TableCell align="center">Description</TableCell>
              <TableCell align="center">Quantity</TableCell>
              <TableCell align="center">Price</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {products.map((item, index) => (
              <TableRow key={index}>
                <TableCell style={styles.tableCell} component="td" scope="row">
                  {" "}
                </TableCell>
                <TableCell style={styles.tableCell} align="center">item</TableCell>
                <TableCell style={styles.tableCell} align="center">
                  {" "}
                  <Select
                    native
                    value={0}
                    margin="dense"
                    //   onChange={handleChange}
                    variant="outlined"
                  >
                    <option aria-label="None" value="" />
                    <option value={10}>10</option>
                    <option value={20}>20</option>
                    <option value={30}>30</option>
                  </Select>
                </TableCell>
                <TableCell style={styles.tableCell} align="center">item</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </div>
    );
  }
}
