import data from "../../data/data.json";
import { Container } from "@material-ui/core";

const styles = {
    table:{
        color: "#212529"
    }
}

export default class CartPage extends Component {
  state = {
    products: [],
    error: false,
  };

  componentDidMount() {
    const items = data.slice(0, 3);
    this.setState({ products: items });
  }

  async addToCart(id, quantity) {
    // Add data to cart api
    // Get The name, price and any special instructions
  }

  async removeFromCart(id, quantity) {
    // Remove data from cart api
  }

  render() {
    const { products } = this.state;

    return (
      <div id="cartPage">
        <Container id="cartContainer">
          <table id="cartTable">
            <thead id="cartHeader">
              <tr>
                <th>Product</th>
                <th>Description</th>
                <th>Quantity</th>
                <th>Price</th>
              </tr>
            </thead>
            <tbody id="cartBody">
              {products.map((item, index) => {
                return (
                  <tr key={index}>
                    <td id="itemImage">itemImage</td>
                    <td id="itemName">itemName</td>
                    <td id="itemQuantity">itemQuantity</td>
                    <td id="itemPrice">itemPrice</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </Container>
      </div>
    );
  }
}
