import React, { Component } from "react";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";
import LocationOnIcon from "@material-ui/icons/LocationOn";
import SearchIcon from "@material-ui/icons/Search";
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
import { IconButton, TextField, Button } from "@material-ui/core";

const styles = {
  navBar: {
    marginLeft: "auto",
    marginRight: "auto",
  },
  actions: {
    float: "right",
  },
  searchBar: {
    display: "flex",
    transition: "4s",
    btn: {
      marginTop: 8,
      marginBottom: 4,
    },
  },
};

export default class NavBar extends Component {
  state = {
    searchbar: false,
  };

  onSearchClick = () => {
    this.setState({ searchbar: !this.state.searchbar });
  };

  render() {
    const { searchbar } = this.state;
    return (
      <div id="NavAndSearch">
        <div id="navBar">
          <AppBar position="static">
            <Toolbar>
              <div id="actionsLeft">
                <span id="zipcode">22042</span> 
                <IconButton id="locationIcon" color="secondary">
                  <LocationOnIcon />
                </IconButton>

                <IconButton
                  id="searchIcon"
                  color="secondary"
                  onClick={() => this.onSearchClick()}
                >
                  <SearchIcon />
                </IconButton>
              </div>
              <div id="title" style={styles.navBar}>
                <h2>E-Commerce Solutions</h2>
              </div>
              <div id="actionsRight">
                <IconButton id="shoppingCartIcon" color="secondary">
                  <ShoppingCartIcon />
                </IconButton>

                <IconButton id="accountIcon" color="secondary">
                  <AccountCircleIcon />
                </IconButton>
              </div>
            </Toolbar>
          </AppBar>
        </div>
        <div
          id="searchBar"
          style={
            searchbar
              ? { marginTop:-20, height: 20, padding: "0px 20px" }
              : { display: "none" }
          }
        >
          <form style={styles.searchBar}>
            <Button
              variant="contained"
              color="primary"
              style={styles.searchBar.btn}
            >
              <SearchIcon />
            </Button>
            <TextField
              id="outlinedSearch"
              variant="outlined"
              margin="dense"
              fullWidth
            />
          </form>
        </div>
      </div>
    );
  }
}
