import React, { Component } from "react";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";
import LocationOnIcon from "@material-ui/icons/LocationOn";
import SearchIcon from "@material-ui/icons/Search";
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
import { IconButton, TextField, Tooltip } from "@material-ui/core";

const styles = {
  navBar: {
    marginLeft: "auto",
    marginRight: "auto",
  },
  actions: {
    float: "right",
  },
};

export default class NavBar extends Component {
  state = {
    searchbar: false,
  };

  componentDidMount(){
    styles.searchContainer.display = 'none'
  }

  onSearchClick = () => {
    const display = this.state.searchbar ;
    // If current state is false, change display to block, else vice versa
    display ? styles.searchContainer.display = 'none' : styles.searchContainer.display = "block"
    this.setState({ searchbar: !this.state.searchbar });
  };

  render() {
    return (
      <div id="NavAndSearch">
        <div id="navBar">
          <AppBar position="static">
            <Toolbar>
              <div id="actionsLeft">
                <span id="zipcode">22042</span>
                <Tooltip title="Location">
                  <IconButton id="locationIcon" color="secondary">
                    <LocationOnIcon />
                  </IconButton>
                </Tooltip>
                <Tooltip title="Search">
                  <IconButton
                    id="searchIcon"
                    color="secondary"
                    onClick={() => this.onSearchClick()}
                  >
                    <SearchIcon />
                  </IconButton>
                </Tooltip>
              </div>
              <div id="title" style={styles.navBar}>
                <h2>E-Commerce Solutions</h2>
              </div>
              <div id="actionsRight" styles={styles.actions}>
                <Tooltip title="Cart">
                  <IconButton id="shoppingCartIcon" color="secondary">
                    <ShoppingCartIcon />
                  </IconButton>
                </Tooltip>
                <Tooltip title="Account">
                  <IconButton id="accountIcon" color="secondary">
                    <AccountCircleIcon />
                  </IconButton>
                </Tooltip>
              </div>
            </Toolbar>
          </AppBar>
        </div>
        <div id="searchBar" style={styles.searchContainer}>
          <form>
            <TextField
              id="outlinedSearch"
              variant="outlined"
              margin="dense"
              fullWidth
            />
          </form>
        </div>
      </div>
    );
  }
}
