import React, { Component } from "react";
import { Container, TextField, Typography, MenuItem } from "@material-ui/core";
import NYStrip from "../../assets/img/NYStrip.jpg";

const styles = {
  container: {
    margin: 50,
  },
  image: {
    marginRight: 20,
  },
  product: {
    marginLeft: 25,
    display: "flex",
  },
  productDetails: {
    display: "block",
    padding: "0 20px",
  },
  select: {
    minWidth: 100,
    marginRight: 10,
  },
};

const quantities = [
  { value: "Ounces", label: "oz" },
  { value: "Pounds", label: "lbs" },
];

export default class ProductPage extends Component {
  state = {
    quantity: "",
    customerRequirements: "",
  };

  onQuantityChange = (event) => {
    this.setState({ quantity: event.target.value });
  };

  render() {
    const { quantity } = this.state;
    return (
      <div id="productPage" style={styles.container}>
        <Container>
          <div id="product" style={styles.product}>
            <div id="productImage" style={styles.image}>
              <img src={NYStrip} alt="productImage" style={{ height: 350 }} />
            </div>
            <div id="productDetails" style={styles.productDetails}>
              <Typography variant="inherit" component="h1">
                New York Strip Steak
              </Typography>
              <Typography
                variant="inherit"
                component="h2"
                style={{ marginTop: 15 }}
              >
                $22
              </Typography>
              <Typography
                variant="inherit"
                component="body"
                style={{ margin: "25px 0 0 0" }}
              >
                One delectable New York Strip Steak. The New York Strip is known
                by other names (Ambassador Steak, Kansas City strip, etc.), but
                its marbling and tenderness deliver one result: fantastic taste.
              </Typography>
              <div style={{ display: "flex" }}>
                <TextField
                  variant="outlined"
                  margin="dense"
                  label="Weight"
                  id="quantity"
                  required
                  placeholder="Enter quantity"
                  style={styles.select}
                />
                <TextField
                  id="outlined-select-quatitiy"
                  select
                  required
                  label="Unit"
                  value={quantity}
                  onChange={(event) => this.onQuantityChange(event)}
                  variant="outlined"
                  margin="dense"
                  style={styles.select}
                >
                  {quantities.map((option) => (
                    <MenuItem key={option.value} value={option.value}>
                      {option.label}
                    </MenuItem>
                  ))}
                </TextField>
              </div>
              <TextField
                id="customer-requirement-form"
                label="Multiline"
                multiline
                rows={4}
                defaultValue="Default Value"
                variant="outlined"
              />
            </div>
          </div>
        </Container>
      </div>
    );
  }
}
