import React, { Component } from "react";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import items from "../../data/data.json";

const styles = {
  paper: {
    padding: 20,
    textAlign: "center",
    color: "gray",
    border: '1px solid blue'
  },
};

export default class ProductsGrid extends Component {
  state = {
    currData: [],
    rowsPerPage: 5,
    colsPerRow: 5,
  };

  chunkData = (arr, size) => {
    let chunkedData = [];
    let index = 0;

    while (index < arr.length) {
      chunkedData.push(arr.slice(index, size + index));
      index += size;
    }
    return chunkedData;
  };

  componentDidMount() {
    const data = this.chunkData([...items], 4);
    this.setState({ currData: data });
  }

  render() {
    const { currData } = this.state;

    return (
      <div>
        <Grid container spacing={3}>
          {[...currData].map((chunk, index) =>
            [...chunk].map((item, index) => (
              <Grid style={styles.paper} item key={index} xs={6} sm={3}>
                {item.name}
              </Grid>
            ))
          )}
        </Grid>
      </div>
    );
  }
}
