import React, { Component } from "react";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";
import LocationOnIcon from "@material-ui/icons/LocationOn";
import SearchIcon from "@material-ui/icons/Search";
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
import { IconButton, TextField, Tooltip, Button } from "@material-ui/core";

const styles = {
  navBar: {
    marginLeft: "auto",
    marginRight: "auto",
  },
  actions: {
    float: "right",
  },
  searchBar: {
    padding: "0px 20px",
  },
};

export default class NavBar extends Component {
  state = {
    searchbar: false,
  };

  onSearchClick = () => {
    this.setState({ searchbar: !this.state.searchbar });
  };

  render() {
    const { searchbar } = this.state;
    return (
      <div id="NavAndSearch">
        <div id="navBar">
          <AppBar position="static">
            <Toolbar>
              <div id="actionsLeft">
                <span id="zipcode">22042</span>
                <Tooltip title="Location">
                  <IconButton id="locationIcon" color="secondary">
                    <LocationOnIcon />
                  </IconButton>
                </Tooltip>
                <Tooltip title="Search">
                  <IconButton
                    id="searchIcon"
                    color="secondary"
                    onClick={() => this.onSearchClick()}
                  >
                    <SearchIcon />
                  </IconButton>
                </Tooltip>
              </div>
              <div id="title" style={styles.navBar}>
                <h2>E-Commerce Solutions</h2>
              </div>
              <div id="actionsRight">
                <Tooltip title="Cart">
                  <IconButton id="shoppingCartIcon" color="secondary">
                    <ShoppingCartIcon />
                  </IconButton>
                </Tooltip>
                <Tooltip title="Account">
                  <IconButton id="accountIcon" color="secondary">
                    <AccountCircleIcon />
                  </IconButton>
                </Tooltip>
              </div>
            </Toolbar>
          </AppBar>
        </div>
        <div
          id="searchBar"
          style={searchbar ? { display: "block" } : { display: "none" }}
        >
          <Button
            variant="contained"
            color="primary"
            startIcon={<SearchIcon />}
            style={{display: "inline"}}
          />
          <form>
            <TextField
              id="outlinedSearch"
              variant="outlined"
              margin="dense"
              style={styles.searchBar}
              fullWidth
            />
          </form>
        </div>
      </div>
    );
  }
}
