import React, { Component } from "react";
import data from "../../data/data.json";
import {
  Table,
  TableBody,
  TableCell,
  TableRow,
  TableHead,
  Select,
} from "@material-ui/core";

const styles = {
  container: {
    paddingLeft: 15,
    paddingRight: 15,
    margin: "50px auto",
    maxWidth: "90%"
  },
  headerCell:{
    padding: '0.75rem',
    fontWeight: "bold",
    textTransform: 'uppercase'
  },
  tableCell: {
    padding: '0.75rem',
    verticalAlign: 'top',
    borderTop: "1px solid #dee2e6",
    fontSize: 16
  },
  image: {
      verticalAlign: 'middle',
      borderStyle: 'none',
      maxWidth: 200,

  }
};

export default class CartPage extends Component {
  state = {
    products: [],
    error: false,
  };

  componentDidMount() {
    const items = data.slice(0, 3);
    this.setState({ products: items });
  }

  async addToCart(id, quantity) {
    // Add data to cart api
    // Get The name, price and any special instructions
  }

  async removeFromCart(id, quantity) {
    // Remove data from cart api
  }

  render() {
    const { products } = this.state;

    return (
      <div id="cartPage" style={styles.container}>
        <Table aria-label="simple table">
          <TableHead>
            <TableRow id='cartColums'>
              <TableCell align="left" style={styles.headerCell} component="h4">Product</TableCell>
              <TableCell align="left" style={styles.headerCell}>Description</TableCell>
              <TableCell align="left" style={styles.headerCell}>Quantity</TableCell>
              <TableCell align="left" style={styles.headerCell}>Price</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {products.map((item, index) => (
              <TableRow key={index}>
                <TableCell style={styles.tableCell} component="td" scope="row" id="itemImage" align="left">
                  <img src={process.env.PUBLIC_URL + item.image} alt="" style={styles.image}/>
                </TableCell>
                <TableCell style={styles.tableCell} align="left" id="itemName">{item.name}</TableCell>
                <TableCell style={styles.tableCell} align="left" id="itemQuantity">
                  <Select
                    native
                    value={20}
                    margin="dense"
                    //   onChange={handleChange}
                    variant="outlined"
                  >
                    <option aria-label="None" value="" />
                    <option value={10}>10</option>
                    <option value={20}>20</option>
                    <option value={30}>30</option>
                  </Select>
                </TableCell>
                <TableCell style={styles.tableCell} align="left" id="itemPrice">${item.price}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </div>
    );
  }
}
