import React, { Component } from "react";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import items from "../../data/data.json";

const styles = {
  paper: {
    padding: 20,
    textAlign: "center",
    color: "gray",
  },
};

export default class ProductsGrid extends Component {
  state = {
    currData: [],
    rowsPerPage: 5,
    colsPerRow: 5,
  };

  chunkData = (arr, size) => {
    let chunkedData = [];
    let index = 0;

    while (index < arr.length) {
      chunkedData.push(arr.slice(index, size + index));
      index += size;
    }
    return chunkedData;
  };

  componentDidMount() {
    const data = this.chunkData([...items], 4);
    this.setState({ currData: data });
  }

  render() {
    const { currData } = this.state;

    return (
      <div>
        <Grid container spacing={3}>
          {[...currData].map((chunk, index) => (
            <Row xs={2} sm={2} md={4} key={index} style={{ display: "flex", flexWrap: "wrap" }}>
            {[...chunk].map((item, index) => (
              <Col key={index} style={styles.column}>
                <Card style={styles.card}>
                  <Card.Img variant="top" src={opc} />
                  <Card.Body style={{ padding: 5 }}>
                    { <Card.Title>{item.name}</Card.Title>}
                  </Card.Body>
                  <Card.Footer>${item.price}</Card.Footer>
                </Card>
              </Col>
            ))}
          </Row>
          ))}
        </Grid>
      </div>
    );
  }
}
