import React, { Component } from "react";
import Grid from "@material-ui/core/Grid";
import items from "../../data/data.json";
import { Container, Typography } from "@material-ui/core";
import opc from "../../assets/img/opc.png";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import CardActionArea from "@material-ui/core/CardActionArea";

const styles = {
  paper: {
    padding: 10,
    textAlign: "center",
    color: "gray",
  },
};

export default class ProductsGrid extends Component {
  state = {
    currData: [],
    rowsPerPage: 5,
    colsPerRow: 5,
  };

  chunkData = (arr, size) => {
    let chunkedData = [];
    let index = 0;

    while (index < arr.length) {
      chunkedData.push(arr.slice(index, size + index));
      index += size;
    }
    return chunkedData;
  };

  componentDidMount() {
    const data = this.chunkData([...items], 4);
    this.setState({ currData: data });
  }

  render() {
    const { currData } = this.state;

    return (
      <div>
        {/* <Container maxWidth={"sm"}> */}
          <Grid container spacing={3}>
            {[...currData].map((chunk, index) =>
              [...chunk].map((item, index) => (
                <Grid
                  style={styles.paper}
                  item
                  key={index}
                  xs={6}
                  sm={4}
                  md={2}
                >
                  <Card>
                    <CardActionArea>
                      <CardMedia
                        style={{ height: 200 }}
                        image={opc}
                        title={item.name}
                      />
                      <CardContent>
                        <Typography gutterBottom variant="h6" component="h3">
                          {item.name}
                        </Typography>
                        <Typography
                          variant="h6"
                          color="textSecondary"
                          component="p"
                        >
                          {`$${item.price}`}
                        </Typography>
                      </CardContent>
                    </CardActionArea>
                  </Card>
                </Grid>
              ))
            )}
          </Grid>
        {/* </Container> */}
      </div>
    );
  }
}
